/**
 * Defines the data structure for a validator object
 * found within the plutus.json file from Aiken.
 */
export interface PlutusValidator {
  /**
   * The title of the validator.
   */
  title: string;
  /**
   * The compiled code of the validator.
   */
  compiledCode: string;
  /**
   * The hash of the validator.
   */
  hash: string;
  // Add other properties if needed, such as 'parameters', 'redeemer', etc.
}

/**
 * Defines the main data structure of the plutus.json file.
 */
export interface PlutusBlueprint {
  /**
   * Preamble information for the blueprint.
   */
  preamble: {
    /**
     * The title of the blueprint.
     */
    title: string;
    /**
     * The version of the blueprint.
     */
    version: string;
    // ...other properties in preamble
  };
  /**
   * An array of validator objects included in the blueprint.
   */
  validators: PlutusValidator[];
  /**
   * Definitions section of the blueprint.
   */
  definitions: Record<string, any>;
}
